import{_ as a,c as t,o as n,a1 as h}from"./chunks/framework.DypyPqwY.js";const d=JSON.parse('{"title":"Bài giảng: Tìm x — Lớp 3","description":"","frontmatter":{},"headers":[],"relativePath":"introduction/chuong1/bai1.md","filePath":"introduction/chuong1/bai1.md","lastUpdated":1757813678000}'),l={name:"introduction/chuong1/bai1.md"};function o(e,i,c,u,r,p){return n(),t("div",null,[...i[0]||(i[0]=[h('<h1 id="bai-giang-tim-x-—-lop-3" tabindex="-1">Bài giảng: Tìm x — Lớp 3 <a class="header-anchor" href="#bai-giang-tim-x-—-lop-3" aria-label="Permalink to &quot;Bài giảng: Tìm x — Lớp 3&quot;">​</a></h1><hr><h3 id="muc-tieu" tabindex="-1">Mục tiêu <a class="header-anchor" href="#muc-tieu" aria-label="Permalink to &quot;Mục tiêu&quot;">​</a></h3><ul><li>Học sinh hiểu ý nghĩa của việc &quot;tìm x&quot; (tìm số bị ẩn trong phép toán đơn giản).</li><li>Học sinh biết cách tìm x trong các phương trình một bước với phép cộng và phép trừ.</li><li>Học sinh có thể kiểm tra lại kết quả bằng cách thay số tìm được vào phương trình.</li></ul><h3 id="chuan-bi" tabindex="-1">Chuẩn bị <a class="header-anchor" href="#chuan-bi" aria-label="Permalink to &quot;Chuẩn bị&quot;">​</a></h3><ul><li>Giáo viên: bảng, phấn, các que tính hoặc hình vuông đếm, phiếu bài tập.</li><li>Học sinh: vở, bút chì, que tính (nếu có).</li></ul><h3 id="thoi-luong" tabindex="-1">Thời lượng <a class="header-anchor" href="#thoi-luong" aria-label="Permalink to &quot;Thời lượng&quot;">​</a></h3><p>40 phút (có thể điều chỉnh tùy mức lớp).</p><hr><h1 id="ke-hoach-bai-day-chi-tiet" tabindex="-1">Kế hoạch bài dạy (chi tiết) <a class="header-anchor" href="#ke-hoach-bai-day-chi-tiet" aria-label="Permalink to &quot;Kế hoạch bài dạy (chi tiết)&quot;">​</a></h1><h2 id="_1-khoi-đong-5-phut" tabindex="-1">1. Khởi động (5 phút) <a class="header-anchor" href="#_1-khoi-đong-5-phut" aria-label="Permalink to &quot;1. Khởi động (5 phút)&quot;">​</a></h2><ul><li>Trò chơi nhanh: &quot;Số bị ẩn&quot; — Giáo viên viết <code>8 = _ + 5</code> lên bảng, gọi học sinh trả lời nhanh.</li><li>Mục đích: kích hoạt suy nghĩ, ôn lại phép cộng và phép trừ.</li></ul><h2 id="_2-gioi-thieu-bai-5-phut" tabindex="-1">2. Giới thiệu bài (5 phút) <a class="header-anchor" href="#_2-gioi-thieu-bai-5-phut" aria-label="Permalink to &quot;2. Giới thiệu bài (5 phút)&quot;">​</a></h2><ul><li>Giải thích khái niệm: &quot;Tìm x&quot; nghĩa là tìm số còn thiếu trong phép toán sao cho hai vế bằng nhau.</li><li>Ví dụ minh họa bằng hình ảnh: một ô số chứa 3 que, ô kia chứa 5 que; tổng cần là 8 que -&gt; ô thiếu là 3 que.</li></ul><h2 id="_3-trinh-bay-vi-du-mau-15-phut" tabindex="-1">3. Trình bày ví dụ mẫu (15 phút) <a class="header-anchor" href="#_3-trinh-bay-vi-du-mau-15-phut" aria-label="Permalink to &quot;3. Trình bày ví dụ mẫu (15 phút)&quot;">​</a></h2><ul><li><strong>Cách làm chung:</strong> nếu phương trình dạng <code>x + a = b</code> thì <code>x = b - a</code>. Nếu phương trình dạng <code>a + x = b</code> thì <code>x = b - a</code>. Nếu phương trình dạng <code>x - a = b</code> thì <code>x = b + a</code>. Nếu phương trình dạng <code>a - x = b</code> thì <code>x = a - b</code>.</li></ul><h3 id="vi-du-co-loi-giai-buoc‐buoc" tabindex="-1">Ví dụ có lời giải (bước‑bước) <a class="header-anchor" href="#vi-du-co-loi-giai-buoc‐buoc" aria-label="Permalink to &quot;Ví dụ có lời giải (bước‑bước)&quot;">​</a></h3><p><strong>Ví dụ 1</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span> x + 5 = 8</span></span></code></pre></div><ul><li>Bước 1: Muốn tìm x, lấy 8 trừ 5.</li><li>Bước 2: x = 8 - 5 = 3.</li><li>Kiểm tra: 3 + 5 = 8 (đúng).</li></ul><p><strong>Ví dụ 2</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>7 + x = 10</span></span></code></pre></div><ul><li>x = 10 - 7 = 3.</li><li>Kiểm tra: 7 + 3 = 10 (đúng).</li></ul><p><strong>Ví dụ 3</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>x - 6 = 4</span></span></code></pre></div><ul><li>Bước 1: Muốn tìm x, lấy 4 cộng 6.</li><li>Bước 2: x = 4 + 6 = 10.</li><li>Kiểm tra: 10 - 6 = 4 (đúng).</li></ul><p><strong>Ví dụ 4</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>15 - x = 9</span></span></code></pre></div><ul><li>x = 15 - 9 = 6.</li><li>Kiểm tra: 15 - 6 = 9 (đúng).</li></ul><p><strong>Ví dụ 5 (nâng cao nhẹ)</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span> x + 4 + 3 = 12</span></span></code></pre></div><ul><li>Lưu ý: trước hết cộng các số đã biết: 4 + 3 = 7.</li><li>Ta có x + 7 = 12 → x = 12 - 7 = 5.</li><li>Kiểm tra: 5 + 4 + 3 = 12 (đúng).</li></ul><p>(Cho thêm 3–4 ví dụ minh họa lên bảng, mỗi ví dụ làm chậm để học sinh quan sát.)</p><h2 id="_4-luyen-tap-10-phut" tabindex="-1">4. Luyện tập (10 phút) <a class="header-anchor" href="#_4-luyen-tap-10-phut" aria-label="Permalink to &quot;4. Luyện tập (10 phút)&quot;">​</a></h2><ul><li>Chia bài tập thành 2 phần: làm miệng (theo cặp) và làm vào vở.</li></ul><h3 id="lam-mieng-theo-cap-3-phut" tabindex="-1">Làm miệng (theo cặp, 3 phút) <a class="header-anchor" href="#lam-mieng-theo-cap-3-phut" aria-label="Permalink to &quot;Làm miệng (theo cặp, 3 phút)&quot;">​</a></h3><ul><li>GV cho mỗi cặp 2–3 thẻ bài (ví dụ: <code>x + 3 = 9</code>, <code>14 - x = 6</code>), các em trao đổi và trả lời.</li></ul><h3 id="lam-bai-vao-vo-5-phut" tabindex="-1">Làm bài vào vở (5 phút) <a class="header-anchor" href="#lam-bai-vao-vo-5-phut" aria-label="Permalink to &quot;Làm bài vào vở (5 phút)&quot;">​</a></h3><ul><li>Học sinh làm 10 bài cơ bản (dưới đây). Giáo viên đi quan sát, hỗ trợ.</li></ul><h2 id="_5-cung-co-dan-do-5-phut" tabindex="-1">5. Củng cố - Dặn dò (5 phút) <a class="header-anchor" href="#_5-cung-co-dan-do-5-phut" aria-label="Permalink to &quot;5. Củng cố - Dặn dò (5 phút)&quot;">​</a></h2><ul><li>Chốt lại quy tắc tìm x cho từng dạng (cộng, trừ).</li><li>Giao bài tập về nhà (phiếu nhỏ).</li></ul><hr><h1 id="bang-vi-du-va-bai-tap-phieu-in" tabindex="-1">Bảng ví dụ và bài tập (phiếu in) <a class="header-anchor" href="#bang-vi-du-va-bai-tap-phieu-in" aria-label="Permalink to &quot;Bảng ví dụ và bài tập (phiếu in)&quot;">​</a></h1><h2 id="vi-du-mau-tom-tat-nhanh" tabindex="-1">Ví dụ mẫu (tóm tắt nhanh) <a class="header-anchor" href="#vi-du-mau-tom-tat-nhanh" aria-label="Permalink to &quot;Ví dụ mẫu (tóm tắt nhanh)&quot;">​</a></h2><ul><li>x + 5 = 8 → x = 8 - 5 = 3.</li><li>7 + x = 10 → x = 10 - 7 = 3.</li><li>x - 6 = 4 → x = 4 + 6 = 10.</li><li>15 - x = 9 → x = 15 - 9 = 6.</li><li>x + 4 + 3 = 12 → x = 12 - 7 = 5.</li></ul><h2 id="bai-tap-tim-x-co-ban" tabindex="-1">Bài tập: Tìm x (cơ bản) <a class="header-anchor" href="#bai-tap-tim-x-co-ban" aria-label="Permalink to &quot;Bài tập: Tìm x (cơ bản)&quot;">​</a></h2><ol><li>x + 3 = 6</li><li>4 + x = 9</li><li>x + 7 = 10</li><li>8 + x = 12</li><li>x - 2 = 5</li><li>9 - x = 4</li><li>x - 6 = 2</li><li>14 - x = 6</li><li>x + 5 = 13</li><li>3 + x = 11</li></ol><h2 id="bai-tap-nang-cao-thu-thach" tabindex="-1">Bài tập: Nâng cao / Thử thách <a class="header-anchor" href="#bai-tap-nang-cao-thu-thach" aria-label="Permalink to &quot;Bài tập: Nâng cao / Thử thách&quot;">​</a></h2><ol start="11"><li>x + 4 + 3 = 12</li><li>2 + x + 5 = 11</li><li>x - 3 + 2 = 6</li><li>10 = x + 2 + 3</li><li>20 - (x + 3) = 12 <em>(giải thích: x + 3 = 20 - 12)</em></li></ol><h2 id="bai-toan-bang-loi" tabindex="-1">Bài toán bằng lời <a class="header-anchor" href="#bai-toan-bang-loi" aria-label="Permalink to &quot;Bài toán bằng lời&quot;">​</a></h2><ol start="16"><li>Lan có x quả táo. Lan lấy thêm 4 quả thì có 9 quả. Viết phương trình và tìm x.</li><li>Một hộp có 12 cây bút. Sau khi lấy ra x cây thì còn 7 cây. Hỏi đã lấy ra bao nhiêu cây? Viết phương trình và giải.</li><li>Minh có 3 gói kẹo, mỗi gói 4 viên. Sau đó Minh nhận thêm x viên kẹo và tổng là 15 viên. Tìm x.</li></ol><hr><h1 id="đap-an" tabindex="-1">Đáp án <a class="header-anchor" href="#đap-an" aria-label="Permalink to &quot;Đáp án&quot;">​</a></h1><p><strong>Cơ bản</strong></p><ol><li>x = 3</li><li>x = 5</li><li>x = 3</li><li>x = 4</li><li>x = 7</li><li>x = 5</li><li>x = 8</li><li>x = 8</li><li>x = 8</li><li>x = 8</li></ol><p><strong>Nâng cao</strong> 11. x = 5 12. x = 4 13. x = 7 <em>(vì x - 3 + 2 = x - 1 = 6 → x = 7)</em> 14. x = 5 15. x = 5 <em>(vì x + 3 = 8 → x = 5)</em></p><p><strong>Bài toán lời</strong> 16. x + 4 = 9 → x = 5 17. 12 - x = 7 → x = 5 18. 3 × 4 + x = 15 → 12 + x = 15 → x = 3</p><hr><h1 id="goi-y-giang-day-loi-thuong-gap" tabindex="-1">Gợi ý giảng dạy &amp; lỗi thường gặp <a class="header-anchor" href="#goi-y-giang-day-loi-thuong-gap" aria-label="Permalink to &quot;Gợi ý giảng dạy &amp; lỗi thường gặp&quot;">​</a></h1><ul><li><strong>Gợi ý giảng dạy:</strong> dùng que tính hoặc hình ảnh để cho học sinh trực quan hóa bài toán. Khi học sinh quen, chuyển sang ghi phương trình và làm tính.</li><li><strong>Lỗi thường gặp:</strong> hay trừ sai chiều (ví dụ viết x = a - b khi thực tế phải x = b - a), quên kiểm tra kết quả.</li><li><strong>Cách khắc phục:</strong> cho học sinh thực hành kiểm tra: thay x vào biểu thức ban đầu để kiểm tra hai vế có bằng nhau không.</li></ul><hr><h1 id="mo-rong-neu-con-thoi-gian" tabindex="-1">Mở rộng (nếu còn thời gian) <a class="header-anchor" href="#mo-rong-neu-con-thoi-gian" aria-label="Permalink to &quot;Mở rộng (nếu còn thời gian)&quot;">​</a></h1><ul><li>Thử tìm x trong phương trình có 3 số: <code>x + 2 + 3 = 11</code>.</li><li>Giới thiệu khái niệm đơn giản về nhân ở dạng toán lời: <code>3 nhóm, mỗi nhóm 4 quả, thêm x → tổng 15</code>.</li></ul><hr><p><em>Phiếu này có thể in trực tiếp hoặc copy sang file <code>.md</code> để chỉnh sửa.</em></p>',65)])])}const g=a(l,[["render",o]]);export{d as __pageData,g as default};
