import{_ as n,c as i,o as h,a1 as a}from"./chunks/framework.dr89wLL-.js";const u=JSON.parse('{"title":"🧭 Bài học Vật Lí: Quãng Đường – Vận Tốc – Thời Gian","description":"","frontmatter":{},"headers":[],"relativePath":"introduction/chuong1/bai3.md","filePath":"introduction/chuong1/bai3.md","lastUpdated":null}'),o={name:"introduction/chuong1/bai3.md"};function g(r,t,c,l,e,d){return h(),i("div",null,[...t[0]||(t[0]=[a('<h1 id="🧭-bai-hoc-vat-li-quang-đuong-–-van-toc-–-thoi-gian" tabindex="-1">🧭 Bài học Vật Lí: Quãng Đường – Vận Tốc – Thời Gian <a class="header-anchor" href="#🧭-bai-hoc-vat-li-quang-đuong-–-van-toc-–-thoi-gian" aria-label="Permalink to &quot;🧭 Bài học Vật Lí: Quãng Đường – Vận Tốc – Thời Gian&quot;">​</a></h1><h2 id="_1️⃣-khai-niem-co-ban" tabindex="-1">1️⃣ Khái niệm cơ bản <a class="header-anchor" href="#_1️⃣-khai-niem-co-ban" aria-label="Permalink to &quot;1️⃣ Khái niệm cơ bản&quot;">​</a></h2><ul><li><strong>Quãng đường (s)</strong>: Độ dài đường đi mà vật đã di chuyển, đo bằng <strong>mét (m)</strong> hoặc <strong>kilômét (km)</strong>.</li><li><strong>Vận tốc (v)</strong>: Độ nhanh hay chậm của chuyển động, đo bằng <strong>mét/giây (m/s)</strong> hoặc <strong>km/giờ (km/h)</strong>.</li><li><strong>Thời gian (t)</strong>: Khoảng thời gian vật chuyển động, đo bằng <strong>giây (s)</strong>, <strong>phút (ph)</strong> hoặc <strong>giờ (h)</strong>.</li></ul><hr><h2 id="_2️⃣-cong-thuc-lien-he" tabindex="-1">2️⃣ Công thức liên hệ <a class="header-anchor" href="#_2️⃣-cong-thuc-lien-he" aria-label="Permalink to &quot;2️⃣ Công thức liên hệ&quot;">​</a></h2><p>Ba đại lượng liên quan với nhau qua công thức:</p><p><strong>s = v × t</strong></p><p>Từ đó suy ra:</p><ul><li><strong>Vận tốc:</strong> v = s / t</li><li><strong>Thời gian:</strong> t = s / v</li></ul><blockquote><p>💡 <strong>Mẹo ghi nhớ:</strong> Hãy tưởng tượng tam giác “SVT”:</p><ul><li>S (quãng đường) ở trên</li><li>V (vận tốc) và T (thời gian) ở dưới</li><li>Lấy S che đi, công thức còn lại là V × T. Tương tự cho V hoặc T.</li></ul></blockquote><hr><h2 id="_3️⃣-đoi-đon-vi-thuong-gap" tabindex="-1">3️⃣ Đổi đơn vị thường gặp <a class="header-anchor" href="#_3️⃣-đoi-đon-vi-thuong-gap" aria-label="Permalink to &quot;3️⃣ Đổi đơn vị thường gặp&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Từ</th><th>Sang</th><th>Cách đổi</th></tr></thead><tbody><tr><td>1 km</td><td>m</td><td>1 km = 1000 m</td></tr><tr><td>1 giờ</td><td>giây</td><td>1 giờ = 3600 s</td></tr><tr><td>m/s</td><td>km/h</td><td>Nhân 3,6</td></tr><tr><td>km/h</td><td>m/s</td><td>Chia 3,6</td></tr></tbody></table><hr><h2 id="_4️⃣-vi-du-minh-hoa" tabindex="-1">4️⃣ Ví dụ minh họa <a class="header-anchor" href="#_4️⃣-vi-du-minh-hoa" aria-label="Permalink to &quot;4️⃣ Ví dụ minh họa&quot;">​</a></h2><p><strong>Ví dụ 1:</strong><br> Một xe máy đi được quãng đường 90 km trong 2 giờ. Tính vận tốc của xe.</p><p>Giải:<br> v = 90 km / 2 h = 45 km/h</p><p><strong>Ví dụ 2:</strong><br> Một người chạy với vận tốc 5 m/s. Hỏi trong 3 phút người đó chạy được bao nhiêu mét?</p><p>Giải:<br> s = 5 × (3 × 60) = 900 m</p><hr><h2 id="_5️⃣-ung-dung-thuc-te" tabindex="-1">5️⃣ Ứng dụng thực tế <a class="header-anchor" href="#_5️⃣-ung-dung-thuc-te" aria-label="Permalink to &quot;5️⃣ Ứng dụng thực tế&quot;">​</a></h2><ul><li>Tính quãng đường khi đi du lịch hoặc chạy bộ.</li><li>Xác định thời gian di chuyển giữa hai thành phố.</li><li>Tính tốc độ trung bình trong thể thao.</li></ul><hr><h2 id="_6️⃣-tom-tat" tabindex="-1">6️⃣ Tóm tắt <a class="header-anchor" href="#_6️⃣-tom-tat" aria-label="Permalink to &quot;6️⃣ Tóm tắt&quot;">​</a></h2><ul><li><strong>Công thức chính:</strong> s = v × t</li><li>Hãy chú ý <strong>đổi đơn vị</strong> trước khi tính toán.</li><li>Hiểu mối liên hệ: tăng vận tốc → quãng đường dài hơn trong cùng thời gian.</li></ul><hr><p>✍️ <em>Soạn bởi [Tên bạn]</em><br> 📅 <em>Ngày soạn: 14/09/2025</em></p>',27)])])}const m=n(o,[["render",g]]);export{u as __pageData,m as default};
