import{_ as t,c as i,o as h,a1 as o}from"./chunks/framework.dr89wLL-.js";const d=JSON.parse('{"title":"Động Lực: Khái Niệm, Nguồn Gốc và Ứng Dụng","description":"","frontmatter":{},"headers":[],"relativePath":"introduction/chuong3/dongluc.md","filePath":"introduction/chuong3/dongluc.md","lastUpdated":null}'),a={name:"introduction/chuong3/dongluc.md"};function c(l,n,g,r,u,e){return h(),i("div",null,[...n[0]||(n[0]=[o('<h1 id="đong-luc-khai-niem-nguon-goc-va-ung-dung" tabindex="-1">Động Lực: Khái Niệm, Nguồn Gốc và Ứng Dụng <a class="header-anchor" href="#đong-luc-khai-niem-nguon-goc-va-ung-dung" aria-label="Permalink to &quot;Động Lực: Khái Niệm, Nguồn Gốc và Ứng Dụng&quot;">​</a></h1><h2 id="_1-đong-luc-la-gi" tabindex="-1">1. Động lực là gì? <a class="header-anchor" href="#_1-đong-luc-la-gi" aria-label="Permalink to &quot;1. Động lực là gì?&quot;">​</a></h2><p>Động lực (Motivation) là <strong>nguồn năng lượng nội tại hoặc ngoại tại</strong> thúc đẩy con người hành động để đạt được mục tiêu.<br> Nói cách khác, động lực chính là <em>lý do</em> khiến chúng ta bắt đầu, duy trì và hoàn thành một công việc.</p><blockquote><p><strong>Định nghĩa ngắn gọn:</strong><br> Động lực = Nhu cầu + Mong muốn + Quyết tâm</p></blockquote><hr><h2 id="_2-phan-loai-đong-luc" tabindex="-1">2. Phân loại động lực <a class="header-anchor" href="#_2-phan-loai-đong-luc" aria-label="Permalink to &quot;2. Phân loại động lực&quot;">​</a></h2><p>Có thể chia thành hai nhóm chính:</p><h3 id="_2-1-đong-luc-noi-tai" tabindex="-1">2.1 Động lực nội tại <a class="header-anchor" href="#_2-1-đong-luc-noi-tai" aria-label="Permalink to &quot;2.1 Động lực nội tại&quot;">​</a></h3><ul><li>Xuất phát từ <strong>bên trong cá nhân</strong>: niềm đam mê, sự tò mò, mong muốn phát triển.</li><li>Ví dụ: Học một ngôn ngữ mới vì yêu thích văn hóa.</li></ul><h3 id="_2-2-đong-luc-ngoai-tai" tabindex="-1">2.2 Động lực ngoại tại <a class="header-anchor" href="#_2-2-đong-luc-ngoai-tai" aria-label="Permalink to &quot;2.2 Động lực ngoại tại&quot;">​</a></h3><ul><li>Xuất phát từ <strong>yếu tố bên ngoài</strong>: tiền thưởng, sự công nhận, áp lực xã hội.</li><li>Ví dụ: Làm việc chăm chỉ để được thăng chức.</li></ul><hr><h2 id="_3-cac-yeu-to-hinh-thanh-đong-luc" tabindex="-1">3. Các yếu tố hình thành động lực <a class="header-anchor" href="#_3-cac-yeu-to-hinh-thanh-đong-luc" aria-label="Permalink to &quot;3. Các yếu tố hình thành động lực&quot;">​</a></h2><ol><li><strong>Nhu cầu cơ bản</strong>: an toàn, sinh tồn, tài chính.</li><li><strong>Mục tiêu rõ ràng</strong>: biết mình muốn gì và tại sao.</li><li><strong>Niềm tin và giá trị cá nhân</strong>: tin vào khả năng, coi trọng kết quả.</li><li><strong>Môi trường hỗ trợ</strong>: gia đình, bạn bè, đồng nghiệp.</li></ol><hr><h2 id="_4-mo-hinh-noi-bat" tabindex="-1">4. Mô hình nổi bật <a class="header-anchor" href="#_4-mo-hinh-noi-bat" aria-label="Permalink to &quot;4. Mô hình nổi bật&quot;">​</a></h2><ul><li><strong>Tháp nhu cầu Maslow</strong>: từ nhu cầu sinh lý, an toàn, xã hội, được tôn trọng đến tự hiện thực hóa.</li><li><strong>Thuyết Tự Quyết (Self-Determination Theory)</strong>: động lực mạnh mẽ đến từ sự tự do, năng lực, và kết nối.</li></ul><hr><h2 id="_5-cach-duy-tri-đong-luc" tabindex="-1">5. Cách duy trì động lực <a class="header-anchor" href="#_5-cach-duy-tri-đong-luc" aria-label="Permalink to &quot;5. Cách duy trì động lực&quot;">​</a></h2><ul><li><strong>Đặt mục tiêu SMART</strong>: Cụ thể, Đo lường được, Khả thi, Thực tế, Có thời hạn.</li><li>Chia nhỏ công việc và theo dõi tiến trình.</li><li>Tự thưởng cho bản thân sau mỗi giai đoạn.</li><li>Duy trì sức khỏe thể chất và tinh thần.</li></ul><hr><h2 id="_6-ung-dung-trong-cuoc-song" tabindex="-1">6. Ứng dụng trong cuộc sống <a class="header-anchor" href="#_6-ung-dung-trong-cuoc-song" aria-label="Permalink to &quot;6. Ứng dụng trong cuộc sống&quot;">​</a></h2><ul><li><strong>Học tập</strong>: Tạo hứng thú, duy trì sự kiên trì với kiến thức mới.</li><li><strong>Công việc</strong>: Tăng năng suất, đạt thành tích cao.</li><li><strong>Phát triển cá nhân</strong>: Xây dựng thói quen lành mạnh, chinh phục mục tiêu dài hạn.</li></ul><hr><h2 id="📝-tom-tat" tabindex="-1">📝 Tóm tắt <a class="header-anchor" href="#📝-tom-tat" aria-label="Permalink to &quot;📝 Tóm tắt&quot;">​</a></h2><p>Động lực là <strong>chìa khóa thành công</strong>. Hiểu và nuôi dưỡng động lực giúp chúng ta:</p><ul><li>Hành động liên tục.</li><li>Vượt qua khó khăn.</li><li>Biến ước mơ thành hiện thực.</li></ul>',27)])])}const m=t(a,[["render",c]]);export{d as __pageData,m as default};
